function enviar() {
    nombre = $('#id_poster').val();
    wsite = $('#id_website').val();
    comentario = $('#id_coment').val();
    date = obtenerFecha();
    hour = obtenerHora();
    if(document.postF.poster.value.length!=0 && document.postF.coment.value.length!=0){
        $.post("/add/",	{
            poster: nombre,
            website: wsite,
            coment: comentario,
            fecha: date,
            hora: hour
        },

        function(data) {
            if (data != "ERROR") {
                var item = '<p class="test"><a href="';
                item += data.poster;
                item += '">'
                item += data.poster;
                item += '</a><span id="dots"> :</span><br />'
                item += data.coment;
                item += '<br /><span class="fecha_hora"><small>';
                item += data.fecha;
                item += '&nbsp;&nbsp;';
                item += data.hora;
                item += '</small></span></p>';
                $("#saved").prepend(item);
                $("#comment-form")[0].reset();
                $('#tracker').html('140');
            }
        }, "json");
    }/*cierro IF*/
    else{
        alert("Introduzca un nombre y un mensaje!");
        return false;
    }
    return false;
}/*cierro la funcion enviar*/

